import React, { useState } from 'react';
import { Container, Grid, List, ListItem, ListItemText, Typography, Button } from '@mui/material';

interface Device {
  id: number;
  name: string;
}

interface App {
  id: number;
  name: string;
}

export const DashboardPage = () => {
  const [selectedDevice, setSelectedDevice] = useState<Device | null>(null);
  const devices: Device[] = [
    { id: 1, name: 'Device 1' },
    { id: 2, name: 'Device 2' },
    { id: 3, name: 'Device 3' },
  ];

  const installedApps: App[] = selectedDevice ? [
    { id: 1, name: 'App 1' },
    { id: 2, name: 'App 2' },
    { id: 3, name: 'App 3' },
  ] : [];

  const availableApps: App[] = [
    { id: 4, name: 'App 4' },
    { id: 5, name: 'App 5' },
    { id: 6, name: 'App 6' },
  ];

  const handleDeviceSelect = (device: Device) => {
    setSelectedDevice(device);
  };

  const handleAppInstall = (app: App) => {
    // Add your installation logic here
    console.log(`Installing app ${app.name} on device ${selectedDevice?.name}`);
  };

  return (
    <Container>
      <Grid container spacing={3}>
        <Grid item xs={3}>
          <Typography variant="h5" gutterBottom>Devices</Typography>
          <List>
            {devices.map(device => (
              <ListItem key={device.id} button onClick={() => handleDeviceSelect(device)}>
                <ListItemText primary={device.name} />
              </ListItem>
            ))}
          </List>
        </Grid>
        <Grid item xs={9}>
          {selectedDevice ? (
            <div>
              <Typography variant="h5" gutterBottom>Installed Apps</Typography>
              <List>
                {installedApps.map(app => (
                  <ListItem key={app.id}>
                    <ListItemText primary={app.name} />
                  </ListItem>
                ))}
              </List>
              <Typography variant="h5" gutterBottom>Available Apps to Install</Typography>
              <List>
                {availableApps.map(app => (
                  <ListItem key={app.id}>
                    <ListItemText primary={app.name} />
                    <Button variant="contained" color="primary" onClick={() => handleAppInstall(app)}>Install</Button>
                  </ListItem>
                ))}
              </List>
            </div>
          ) : (
            <Typography variant="body1">Select a device to view its apps</Typography>
          )}
        </Grid>
      </Grid>
    </Container>
  );
};
