export const DashboardPage= () => {
  const devices: Device[] = [
    { id: 1, name: 'Device 1' },
    { id: 2, name: 'Device 2' },
    { id: 3, name: 'Device 3' },
  ];

  const [selectedDevice, setSelectedDevice] = useState<Device | null>(devices[0]);

  const installedApps: App[] = selectedDevice ? [
    { id: 1, name: 'App 1' },
    { id: 2, name: 'App 2' },
    { id: 3, name: 'App 3' },
  ] : [];

  const availableApps: App[] = [
    { id: 4, name: 'App 4' },
    { id: 5, name: 'App 5' },
    { id: 6, name: 'App 6' },
  ];

  const handleDeviceSelect = (device: Device) => {
    setSelectedDevice(device);
  };

  const handleAppInstall = (app: App) => {
    // Add your installation logic here
    console.log(`Installing app ${app.name} on device ${selectedDevice?.name}`);
  };

  return (
    <div className="flex">
      <div className="w-1/4 bg-gray-200 p-4">
        <h2 className="text-lg font-bold mb-4">Devices</h2>
        <ul>
          {devices.map(device => (
            <li key={device.id} className={`cursor-pointer ${selectedDevice === device ? 'font-bold' : ''}`} onClick={() => handleDeviceSelect(device)}>
              {device.name}
            </li>
          ))}
        </ul>
      </div>
      <div className="w-3/4 p-4">
        {selectedDevice ? (
          <div>
            <h2 className="text-lg font-bold mb-4">Installed Apps</h2>
            <ul>
              {installedApps.map(app => (
                <li key={app.id}>{app.name}</li>
              ))}
            </ul>
            <h2 className="text-lg font-bold my-4">Available Apps to Install</h2>
            <ul>
              {availableApps.map(app => (
                <li key={app.id}>
                  {app.name}{' '}
                  <button className="bg-blue-500 text-white px-2 py-1 rounded" onClick={() => handleAppInstall(app)}>
                    Install
                  </button>
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <div>
            <p>Select a device to view its apps</p>
          </div>
        )}
      </div>
    </div>
  );
};
