import { useState } from "react";
import { LoginPage } from "./LoginPage";
import { RegisterPage } from "./RegisterPage";
import { DashboardPage } from "./DashboardPage";


export const Main = () => {


    const [count, setCount] = useState<number>(() => {
        const savedCount = window.localStorage.getItem('count');
        return savedCount ? parseInt(savedCount, 10) : 0;
      });

  const [currPage, setCurrPage] = useState<string>("LoginPage");
  window.localStorage.setItem('currPage', currPage);

  const moveToRegister = () => {};

  const loginCallback = (curr: string) => {
    setCurrPage(curr)
  }

  return (
    <div>
      {currPage === "LoginPage" ? (
        <LoginPage callback={loginCallback} />
      ) : (
        <></>
      )}
      {currPage === "RegisterPage" ? (<RegisterPage callback={loginCallback} />) : <></>}
      {currPage === "DashboardPage" ? (<DashboardPage callback={loginCallback} />) : <></>}
    </div>
  );
};
